import React, { Fragment } from 'react';
import ReactDOM from "react-dom"
import Card from './Card';
import Button from './Button';
import classes from './ErrorModal.module.css';

const Overlay = props => {
  <Card className={classes.modal}>
  <header className={classes.header}>
    <h2>{props.title}</h2>
  </header>
  <div className={classes.content}>
    <p>{props.message}</p>
  </div>
  <footer className={classes.actions}>
    <Button onClick={props.onConfirm}>Okay</Button>
  </footer>
</Card>
};
const Backdrop = props => <div className={classes.backdrop} onClick={props.onConfirm} />;
const ErrorModal = (props) => {
  return (
    <Fragment>
      {ReactDOM.createPortal(<Overlay title={props.title} message={props.message} onClick={props.o}></Overlay>,document.getElementById("overlay-root"))}
      {ReactDOM.createPortal(<Backdrop></Backdrop>,document.getElementById("backdrop-root"))}
    </Fragment>
  );
};

export default ErrorModal;
